import React, { useState, useEffect } from 'react';
import { connect } from 'nats.ws';

const ChatApp = () => {
  const [username, setUsername] = useState('');
  const [message, setMessage] = useState('');
  const [chat, setChat] = useState([]);
  let natsClient = null;

  useEffect(() => {
    const initializeNats = async () => {
      // Only initialize the connection and subscription if not already established
      if (!natsClient) {
        console.log(natsClient)
        natsClient = await connect({
          servers: 'ws://localhost:9090',
          payload: 'json',
        });
  
        const subscription = natsClient.subscribe('chat', (msg) => {
          const newMessage = msg.data;
          console.log('Received message:', newMessage);
          setChat((prevChat) => [...prevChat, newMessage]);
        });
  
        console.log('Subscription established:', subscription);
      }
    };
  
    initializeNats();
  
    // Cleanup function
    return () => {
      // Close the connection when the component unmounts
      if (natsClient) {
        natsClient.close();
      }
    };
  }, []); // Empty dependency array ensures this effect runs only once on mount
  

  const sendMessage = async () => {
    if (username && message) {
      console.log('Sending message:', { username, message });
      natsClient.publish('chat', JSON.stringify({ username, message }));
      setMessage('');
    }
  };
  

  return (
    <div>
      <div>
        <input
          type="text"
          placeholder="Enter your username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
      </div>
      <div>
        <textarea
          placeholder="Type your message..."
          value={message}
          onChange={(e) => setMessage(e.target.value)}
        />
      </div>
      <button onClick={sendMessage}>Send</button>
      <div>
        <h2>Chat</h2>
        <ul>
          {chat.map((msg, index) => (
            <li key={index}>
              <strong>{msg.username}</strong>: {msg.message}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default ChatApp;
