import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { StringCodec, connect } from 'nats.ws';
import axios from 'axios';
import './chatApp.css'

const ChatApp = () => {
  const [usersData, setUsersData] = useState([]);
  const [ud, setUd] = useState(null);
  const [currid,setCurrid]=useState('')
  const [selectedUserId, setSelectedUserId] = useState(null);
  const [messagesData, setMessagesData] = useState({});
  const [newMessage, setNewMessage] = useState('');
  const navigate = useNavigate();

  const [nc, setConnection] = useState(undefined);
  const [lastError, setLastError] = useState("");
  const [receivedMessages, setReceivedMessages] = useState([]);
  const [sendMessage, setSendMessage] = useState("");
  const sc = StringCodec();

  useEffect(() => {
    const sud = localStorage.getItem('userData');
    const parseud = JSON.parse(sud);
    setUd(parseud);
    setCurrid(parseud['user']['_id'])
    console.log(currid)
    console.log(parseud['user']['email']);
    

    // Fetch users data from the API
    axios.get('http://localhost:7000/user')
      .then((response) => {
        setUsersData(response.data);
      })
      .catch((error) => {
        console.error('Error fetching users:', error);
      });
      console.log(nc)
      if (nc === undefined) {
        c
        const connectToNats = async () => {
          try {
            const natsConnection = await connect({
              servers: "http://localhost:9090",
              headers: {
                "Access-Control-Allow-Origin": "http://localhost:3000",
                "Access-Control-Allow-Methods": "GET, POST, PUT, DELETE, OPTIONS",
                "Access-Control-Allow-Headers": "Origin, X-Requested-With, Content-Type, Accept",
              },
            });
            setConnection(natsConnection);
            console.log(`chat.${currid}.${selectedUserId}`)
            const subscription = natsConnection.subscribe(`chat.${currid}.${selectedUserId}`, {
              callback: (err, msg) => {
                if (err) {
                  console.error(err);
                } else {
                  console.log(message)
                  const message = sc.decode(msg.data);
                  console.log("Received message:", message);
                  setReceivedMessages((prevMessages) => [...prevMessages, message]);
                }
              },
            });
            console.log(subscription);
  
            return () => subscription.unsubscribe();
          } catch (err) {
            setLastError("Error connecting");
            console.error(err);
          }
        };
  
        connectToNats();
      }

  }, [nc]);

  const handleUserClick = (userId) => {
    console.log(userId)
    setSelectedUserId(userId);
  };

  const handleSendMessage = () => {
    if (newMessage.trim() === '') {
      return; // Don't send empty messages
    }
  
    if (nc) {
      const subject = `chat.${currid}.${selectedUserId}`;
      nc.publish(subject, sc.encode(newMessage));
      console.log(newMessage);
    } else {
      console.error("Not connected to NATS");
    }
  
    const updatedMessages = { ...messagesData };
    const newMessageObject = {
      id: Date.now(), // Using timestamp as a unique ID for simplicity
      text: newMessage,
    };
  
    if (selectedUserId in updatedMessages) {
      updatedMessages[selectedUserId] = [
        ...updatedMessages[selectedUserId],
        newMessageObject,
      ];
    } else {
      updatedMessages[selectedUserId] = [newMessageObject];
    }
  
    setMessagesData(updatedMessages);
    setNewMessage('');
  };
  

  return (
    <div className="chat-container">
      <div className="user-list">
        <h2>User List</h2>
        <h2>Current user name {ud ? ud['user']['username'] : ''}</h2>
        <ul>
          {usersData.map((user) => (
            <li key={user._id} onClick={() => handleUserClick(user._id)}>
              {user.username}
            </li>
          ))}
        </ul>
      </div>

      <div className="message-section">
        <h2>Messages</h2>
        {selectedUserId ? (
          <div>
            <ul>
              {receivedMessages[selectedUserId]?.map((message) => (
                <li key={message.id}>{message.text}</li>
              ))}
            </ul>
            <div className="message-input">
              <input
                type="text"
                value={newMessage}
                onChange={(e) => setNewMessage(e.target.value)}
                placeholder="Type your message..."
              />
              <button onClick={handleSendMessage}>Send</button>
            </div>
          </div>
        ) : (
          <p>Select a user to view messages.</p>
        )}
      </div>
    </div>
  );
};

export default ChatApp;
