import { useState, useEffect } from 'react';
import { StringCodec, connect } from 'nats.ws';

// ... (other imports)

function App() {
  const [nc, setConnection] = useState(undefined);
  const [lastError, setLastError] = useState("");
  const [receivedMessages, setReceivedMessages] = useState([]);
  const [sendMessage, setSendMessage] = useState("");
  const sc = StringCodec();

  useEffect(() => {
    if (nc === undefined) {
      const connectToNats = async () => {
        try {
          const natsConnection = await connect({
            servers: "http://localhost:9090"
          });
          setConnection(natsConnection);
          console.log(natsConnection)

          // const subscription = natsConnection.subscribe("trail", (err, msg) => {
          //   if (err) {
          //     console.error(err);
          //   } else {
          //     const decodedMessage = sc.decode(msg.data);
          //     console.log(decodedMessage)
          //     setReceivedMessages((prevMessages) => [...prevMessages, decodedMessage]);
          //   }
          //   console.log(msg.data)
          // });
          nc.subscribe("trail",{
            callback:(err,msg)=>{
              if(err){
                console.log(err)
              }
              else{
                console.log(sc.decode(msg.data))
              }
            },
            max:1,
          })


          return () => subscription.unsubscribe();
        } catch (err) {
          setLastError("Error connecting");
          console.error(err);
        }
      };

      connectToNats();
    }
  }, [nc,sendMessage]);

  const state = nc ? "Connected" : "Not Connected";

  const handleSend = () => {
    if (nc) {
      const subject = "trail";
      nc.publish(subject, sc.encode(sendMessage));
    } else {
      console.error("Not connected to NATS");
    }
  };

  return (
    <div className="App">
      <h1>Kartheek</h1>
      <h1>{state}</h1>
      <h3>{lastError ? lastError : ""}</h3>
      <div>
        <p>Received Messages:</p>
        <ul>
          {receivedMessages.map((message, index) => (
            <li key={index}>{message}</li>
          ))}
        </ul>
      </div>
      <input
        type="text"
        value={sendMessage}
        onChange={(e) => setSendMessage(e.target.value)}
        placeholder="Type your message"
      />
      <button onClick={handleSend}>Send Message</button>
    </div>
  );
}

export default App;
