// ChatApp.js
import React, { useState, useEffect } from 'react';

const ChatApp = () => {
  // State hooks for user data and selected user ID
  const [usersData, setUsersData] = useState([]);
  const [selectedUserId, setSelectedUserId] = useState(null);
  // State hook for messages data
  const [messagesData, setMessagesData] = useState({});

  // Hardcoded user data
  const hardcodedUsers = [
    { id: 1, name: 'John' },
    { id: 2, name: 'Alice' },
    { id: 3, name: 'Bob' },
  ];

  // Hardcoded messages data
  const hardcodedMessages = {
    1: [
      { id: 1, text: 'Hi, how are you?' },
      { id: 2, text: "I'm good, thanks!" },
    ],
    2: [
      { id: 1, text: 'Hello there!' },
      { id: 2, text: "How's it going?" },
    ],
    3: [
      { id: 1, text: 'Hey!' },
      { id: 2, text: "I'm doing well, thanks." },
    ],
  };

  // useEffect hook to set initial user and message data
  useEffect(() => {
    setUsersData(hardcodedUsers);
    setMessagesData(hardcodedMessages);
  }, []);

  // Event handler for clicking on a user
  const handleUserClick = (userId) => {
    setSelectedUserId(userId);
  };

  return (
    <div>
      <div>
        <h2>User List</h2>
        <ul>
          {usersData.map((user) => (
            <li key={user.id} onClick={() => handleUserClick(user.id)}>
              {user.name}
            </li>
          ))}
        </ul>
      </div>

      <div>
        <h2>Messages</h2>
        {selectedUserId ? (
          <ul>
            {messagesData[selectedUserId].map((message) => (
              <li key={message.id}>{message.text}</li>
            ))}
          </ul>
        ) : (
          <p>Select a user to view messages.</p>
        )}
      </div>
    </div>
  );
};

export default ChatApp;
